package com.syntax.class06;

public class Swichbreak {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
String Today;
int day=2;
switch (day){
case 1:
	Today ="Monday";
	break;
case 2:
	Today="Tuesday";
	break;
case 3:
	Today ="Thuesday";
	break;
case 4:
	Today ="Friday";
break;
case 5:
	Today ="Saturday";
	break;
case 6:
	Today ="Sunday";
	break;
case 7:
	Today ="wedensday";
	break;
default:
	Today="invalid";
}
	}

}
