package com.syntax.class07;

public class Whilevsdowhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int num =11;
while(num<=5){
System.out.println("Hello");
num++;
}
System.out.println("_______using do while loop___________");
int num1=6;
do {
	System.out.println("Hello");
	num1++;
}while(num1<=5);
		
		
		
	}

}
